import React, {useState} from 'react';
import ExpenseItem from "./ExpenseItem";
import './ExpensesItemBase.css';
import Card from "../UI/Card";
import ExpensesFilter from "../ExpenseFilter/ExpensesFilter";

function ExpensesItemBase(props) {
    const [enteredYear, setEnteredYear] = useState('2020');

    const selectedYearHandler = selectedYear => {
        setEnteredYear(selectedYear)
    };

    const filteredExpensesPerYear = props.items.filter(expense => {
        return expense.date.getFullYear().toString() === enteredYear;
    });

    return (
        <div>

            <Card className="expenses">
                <ExpensesFilter selected={enteredYear} onSelectedYear={selectedYearHandler}/>
                {props.data.filter(year => year.year).map((expense) => (<ExpenseItem key={expense.id}
                                                           title={expense.title} amount={expense.amount}
                                                         date={expense.date}/>))}
            </Card>
        </div>
    )
        ;
}

export default ExpensesItemBase;
